#!/usr/bin/env bash # # Copyright 2019 (c) roc # # This script shows processes holding the inotify fd, alone with HOW MANY directories each inotify fd watches(0 will be ignored). total=0 result="EXE PID FD-INFO INOTIFY-WATCHES
" while read pid fd; do \ exe="$(readlink -f /proc/$pid/exe || echo n/a)"; \ fdinfo="/proc/$pid/fdinfo/$fd" ; \ count="$(grep -c inotify "$fdinfo" || true)"; \ if [ $((count)) != 0 ]; then total=$((total+count)); \ result+="$exe $pid $fdinfo $count
"; \ fi done &lt;&lt;&lt; "$(lsof +c 0 -n -P -u root|awk &#039;/inotify$/ { gsub(/[urw]$/,"",$4); print $2" "$4 }&#039;)" && echo "total $total inotify watches" && result="$(echo -e $result|column -t)
" && echo -e "$result" | head -1 && echo -e "$result" | sed "1d" | sort -k 4rn;

